mapper.select:
select
id, name, email, gender, birthday, phone, password, register_time, signature, img_url
from userSchema


        and id = #{id}


        and name = #{name}


        and email = #{email}


        and gender = #{gender}


        and birthday = #{birthday}


        and phone = #{phone}


        and password = #{password}


        and register_time = #{registerTime}


        and signature = #{signature}


        and img_url = #{imgUrl}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from userSchema


        and id = #{id}


        and name = #{name}


        and email = #{email}


        and gender = #{gender}


        and birthday = #{birthday}


        and phone = #{phone}


        and password = #{password}


        and register_time = #{registerTime}


        and signature = #{signature}


        and img_url = #{imgUrl}
